mapper.insert:
insert into account (username, password,
  enabled
  )
values (#{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR},
  #{enabled,jdbcType=TINYINT}
  )

mapper.update:
update account
set username = #{username,jdbcType=VARCHAR},
  password = #{password,jdbcType=VARCHAR},
  enabled = #{enabled,jdbcType=TINYINT}
where id = #{id,jdbcType=BIGINT}

update account
set enabled = 1
where id = #{id,jdbcType=BIGINT}

update account
set enabled = 2
where id = #{id,jdbcType=BIGINT}

mapper.select:
select id, username, password, enabled, create_time, update_time
from account
where id = #{id,jdbcType=BIGINT}

select id, username, password, enabled, create_time, update_time
from account
where username = #{username,jdbcType=VARCHAR}

SELECT t1.id, t1.username, t1.password, t1.enabled, t1.create_time, t1.update_time FROM account AS t1
JOIN
( SELECT id FROM account where 1=1

  and username = #{username}


  and enabled = #{enabled}



    ORDER BY create_time


    ORDER BY update_time


    ORDER BY ${ordername}


${order} LIMIT #{offSet} , #{pageSize}) AS t2
WHERE t1.id = t2.id

  and username = #{username}


  and enabled = #{enabled}



    ORDER BY create_time


    ORDER BY update_time


    ORDER BY ${ordername}


${order}  LIMIT #{pageSize}

select count(id)
from account
where 1=1

  and username = #{username}


  and enabled = #{enabled}

ORDER BY #{ordername} #{order}
